import React, { useEffect, useState } from "react";
import Select from 'react-select';
import NewRow from "./NewRow";

const Invoice = () => {




    const [data, setData] = useState([]);
    const [username, setUSername] = useState("");
    const [clicked, setClicked] = useState(false);

    let arr = [];

    const op2 = [
        { label: "ME Torge (Avg.)", value: "ME Torge (Avg.)" },
        { label: "ME Speed (Avg.)", value: "ME Speed (Avg.)" },
        { label: "ME Shaft Power(Avg.)", value: "Rate of Return (Avg.)" }
    ];

    let options = JSON.parse(localStorage.getItem("names"));

    if (options !== null) {
        for (let i = 0; i < options.length; i++) {
            arr.push({ label: options[i], value: options[i] });
        }
    }

    const handleChange = (opt) => {
        let val = opt['value'];
        console.log("val", val);
        setUSername(val);

        let data2 = JSON.parse(localStorage.getItem("data"));
        if (data2) {
            let renderData = data2[val];
            console.log("Kkkkkkkkkkk", renderData);
            setData(renderData);
            rows(renderData);
        }



        // this.setState({ selectedOption: [val.toString()] });
    };

    const onItemAdd = (name, desc, qty, price) => {
        console.log("triggered");
        localStorage.setItem(JSON.stringify("data", { username: [{ name: name, desc: desc, qty: qty, price, price }] }));
    }

    const rows = () => {
        return (
            <div>
                {
                    data ?
                        <>
                            {
                                data.map((item, index) => (
                                    <NewRow key={index} name={item.name} desc={item.desc} qty={item.qty} price={item.price} />

                                ))

                            }
                        </> :
                        <>
                            <NewRow onsubmit={onItemAdd} />
                            <button className="submit" >Add Item</button>
                        </>
                }
            </div>
        );
    }




    return (
        <div>
            <div className="invoice">
                <div className="invoice-input">
                    <input className="mr" type="text" placeholder="Date" ></input>
                    <input className="mr" type="text" placeholder="Due Date" ></input>
                    <Select
                        className="singleselect"
                        options={arr}
                        onChange={handleChange}
                    />
                </div>
                {rows()}

                <NewRow onsubmit={onItemAdd}/>
                <button className="submit" onClick={() => {setClicked(true)}}>Add Item</button>
            </div>
        </div>
    );
}

export default Invoice;
