import React, { useEffect, useState } from "react";
import Select from 'react-select';
import NewRow from "./NewRow";

const Invoice = () => {

    const [data, setData] = useState([]);

    let arr = [];

    const op2 = [
        { label: "ME Torge (Avg.)", value: "ME Torge (Avg.)" },
        { label: "ME Speed (Avg.)", value: "ME Speed (Avg.)" },
        { label: "ME Shaft Power(Avg.)", value: "Rate of Return (Avg.)" }
    ];

    let options = JSON.parse(localStorage.getItem("names")) ;

    if(options !==  null) {
        for(let i=0;i<options.length;i++) {
            arr.push({label : options[i], value : options[i]});
        }
    }

    const handleChange = (opt) => {
        let val = opt['value'];
        console.log(val);

        let data2 = JSON.stringify(localStorage.getItem("data"));
        let renderData = data2[val];
        console.log("Kkkkkkkkkkk",data2);
        setData(renderData);
        rows(data);

        // this.setState({ selectedOption: [val.toString()] });
    };

    const rows = (data) => {
        return(
            <div>
                {
                    data ?
                    <>
                    {
                        data.map((item, index) => (
                        <NewRow />
                        ))
                    }
                    </> : 
                    <>
                    <NewRow/>
                    </>
                }
            </div>
        );
    }




    return (
        <div>
            <form className="invoice">
                <div className="invoice-input">
                    <input className="mr" type="text" placeholder="Date"></input>
                    <input className="mr" type="text" placeholder="Due Date"></input>
                    <Select
                        className="singleselect"
                        options={arr}
                        onChange={handleChange}
                    />
                </div>
                {rows()}

                {/* <NewRow /> */}
                <button className="submit">Add Item</button>
            </form>
        </div>
    );
}

export default Invoice;
